-- =====================================================
-- VERIFICATION QUERIES
-- =====================================================

-- View timetable for Grade 1, Section A
SELECT 
  CASE day_of_week
    WHEN 1 THEN 'Monday'
    WHEN 2 THEN 'Tuesday'
    WHEN 3 THEN 'Wednesday'
    WHEN 4 THEN 'Thursday'
    WHEN 5 THEN 'Friday'
  END AS day,
  period_number,
  s.name AS subject,
  start_time,
  end_time,
  room_number
FROM timetables t
JOIN subjects s ON t.subject_id = s.id
WHERE t.class_id = 'c1000000-0000-0000-0000-000000000001'
  AND t.section_id = 's1000000-0000-0000-0000-000000000001'
ORDER BY day_of_week, period_number;

-- Count timetable entries by class
SELECT 
  c.name AS class_name,
  COUNT(*) AS total_periods
FROM timetables t
JOIN classes c ON t.class_id = c.id
WHERE t.tenant_id = '00000000-0000-0000-0000-000000000001'
GROUP BY c.name
ORDER BY c.name;

-- View teacher schedule for a specific teacher
SELECT 
  CASE day_of_week
    WHEN 1 THEN 'Monday'
    WHEN 2 THEN 'Tuesday'
    WHEN 3 THEN 'Wednesday'
    WHEN 4 THEN 'Thursday'
    WHEN 5 THEN 'Friday'
  END AS day,
  period_number,
  c.name AS class,
  sec.name AS section,
  s.name AS subject,
  start_time,
  end_time,
  room_number
FROM timetables t
JOIN classes c ON t.class_id = c.id
JOIN sections sec ON t.section_id = sec.id
JOIN subjects s ON t.subject_id = s.id
WHERE t.teacher_id = 'sf100000-0000-0000-0000-000000000003' -- English teacher
ORDER BY day_of_week, start_time;

-- =====================================================
-- STATISTICS
-- =====================================================

SELECT 
  'Total Timetable Entries' AS metric,
  COUNT(*) AS count
FROM timetables
WHERE tenant_id = '00000000-0000-0000-0000-000000000001';

SELECT 
  'Classes with Timetable' AS metric,
  COUNT(DISTINCT class_id) AS count
FROM timetables
WHERE tenant_id = '00000000-0000-0000-0000-000000000001';

SELECT 
  'Teachers Assigned' AS metric,
  COUNT(DISTINCT teacher_id) AS count
FROM timetables
WHERE tenant_id = '00000000-0000-0000-0000-000000000001'
  AND teacher_id IS NOT NULL;

-- =====================================================
-- END OF TIMETABLE TEST DATA
-- =====================================================

COMMENT ON TABLE timetables IS 'Test data includes full weekly schedules for Grade 1 (both sections) and sample schedule for Grade 2';
